import SwiftUI
import MapKit

struct Location: Hashable{
    let nome: String
    let foto: String
    let descricao: String
    let latitude: Double
    let longitude: Double
}
struct ContentView: View {
    @State private var vetor = [Location(nome: "Presidente Prudente", foto: "https://lh3.googleusercontent.com/gps-cs-s/AC9h4nq7MKUi4-m2oD8ysZPwt1jaoG3wzMUGSfL0jI1Ya2heXeCVdtN9AwgLNf-Kfb_rq9xnojx9Qk5GauZbE1fZgTEu51KmcR-KD82D2mpdfv07mOWC_PoT6S6KRhean5oLDQBxbzVEFw=w1080-h624-n-k-no", descricao: "Cidade Maravilhosa", latitude:  -22.121265, longitude: -51.383400),
        Location(nome: "Cristo Redentor", foto: "data:image/jpeg;base64,", descricao: "O Cristo Redentor é uma estátua icônica de Jesus Cristo localizada no topo do morro do Corcovado, no Rio de Janeiro, Brasil. É um dos principais símbolos do cristianismo e um marco cultural do Brasil, conhecido mundialmente. A estátua, com seus 38 metros de altura, oferece vistas panorâmicas deslumbrantes da cidade e é considerada uma das Sete Maravilhas do Mundo Moderno.", latitude: -22.951916, longitude: -43.210466),
        Location(nome: "Coliseu Romano", foto: "data:image/jpeg;base64,", descricao: "O Coliseu, também conhecido como Anfiteatro Flaviano, é um antigo anfiteatro localizado no centro da cidade de Roma, Itália. É um dos monumentos mais famosos e importantes do Império Romano e um dos principais pontos turísticos de Roma.", latitude: 41.890209, longitude: 12.492231)
                                
    ]
    
    @State private var vetorPicker = Location(nome: "Presidente Prudente", foto: "", descricao: "Cidade Maravilhosa", latitude: -22.95, longitude: -43.210)
    @State private var cidade = Location(nome: "Presidente Prudente", foto: "", descricao: "Cidade Maravilhosa", latitude: -22.95, longitude: -43.210)
    @State private var aux = Location(nome: "Presidente Prudente", foto: "", descricao: "Cidade Maravilhosa", latitude: -22.95, longitude: -43.210)
    @State private var position = MapCameraPosition.region(MKCoordinateRegion(
        center: CLLocationCoordinate2D(latitude:  -22.121265, longitude: -51.383400),
        span: MKCoordinateSpan(latitudeDelta: 1, longitudeDelta: 1)))
    
    @State private var show = false
    var body: some View {
        ZStack{
            Map(position: $position){
                ForEach(vetor, id: \.self){ c in
                    Annotation("", coordinate: CLLocationCoordinate2D(latitude: c.latitude, longitude: c.longitude), anchor: .bottom) {
                        VStack{
                            Image(systemName: "mappin.circle.fill").resizable().frame(width: 40, height: 40).foregroundColor(/*@START_MENU_TOKEN@*/.blue/*@END_MENU_TOKEN@*/)
                        }.onTapGesture {
                            show = true
                            aux = c
                        }
                    }

                    }
            }.scaledToFill()
                .ignoresSafeArea()
                .sheet(isPresented: $show){
                SheetView(escolhida: $aux)
            }
            VStack{
                ZStack{
                    Picker("", selection: $vetorPicker){
                        ForEach(vetor, id: \.self){ c in
                            Text("\(c.nome)").foregroundStyle(.black)
                        }
                    }.accentColor(.black).frame(width: 270, height: 45).background(Color.fundo).foregroundColor(.black).cornerRadius(20).shadow(color: .black, radius: 2.5)
                    
                        .onChange(of: vetorPicker){
                            position = MapCameraPosition.region(MKCoordinateRegion(
                                center: CLLocationCoordinate2D(latitude: vetorPicker.latitude, longitude: vetorPicker.longitude),
                                span: MKCoordinateSpan(latitudeDelta: 1, longitudeDelta: 1)))
                        }
                    
                }.scaledToFit()
                Spacer()
                Text("Maravilhas do Mundo Moderno").frame(width: 270, height: 45).background(Color.fundo).foregroundColor(.black).cornerRadius(20).shadow(color: /*@START_MENU_TOKEN@*/.black/*@END_MENU_TOKEN@*/, radius: 2.5)
            }.padding()
        }
    }
}

